document.querySelector(".back-to-top").addEventListener("click",function(){window.scrollTo({top:0,behavior:"smooth"})});window.addEventListener("scroll",function(){const a=document.querySelector(".back-to-top");if(window.scrollY>500){a.style.display="block"}else{a.style.display="none"}});const navLinks=document.querySelectorAll(".nav-link");navLinks.forEach(function(a){a.addEventListener("click",function(){navLinks.forEach(function(b){b.classList.remove("active")});this.classList.add("active")})});document.querySelector(".contact-form").addEventListener("submit",function(a){a.preventDefault();alert("感谢您的留言！我们将尽快与您联系。");this.reset()});document.querySelector(".back-to-top").style.display="none";
